import React from 'react';

const styles = {
  html: {
    scrollBehavior: 'smooth',
  },
  body: {
    backgroundColor: '#0C222F',
    backgroundImage: 'url(./components/img/)',
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
  },
  header__logo: {
    paddingTop: '24px',
    paddingLeft: '20px',
  },
  header__linkArea: {
    padding: '15px 0',
  },
  header: {
    position: 'relative',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    minWidth: '320px',
  },
  vector: {
    position: 'absolute',
    width: '1500px',
    height: '0px',
    left: '300px',
    top: '105px',
    border: '2px solid #E4E2DD',
    transform: 'rotate(0.01deg)',
  },
  title: {
    fontWeight: '700',
    fontSize: '28px',
    lineHeight: '1.18',
    textAlign: 'center',
  },
  pageHeader: {
    display: 'flex',
  },
  pageHeader__container: {
    display: 'flex',
    alignItems: 'center',
  },
  siteNav: {
    display: 'flex',
    gap: '50px',
    listStyle: 'none',
  },
  siteNav__item: {
    marginBottom: '32px',
  },
  siteNav__link: {
    fontFamily: `'Josefin Sans', sans-serif`,
    fontStyle: 'normal',
    fontWeight: '600',
    fontSize: '20px',
    lineHeight: '28px',
    color: '#E4E2DD',
    textDecoration: 'none',
  },
  hero: {
    width: '1440px',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    textAlign: 'center',
  },
  hero__title: {
    color: 'white',
    fontFamily: `'Josefin Sans', sans-serif`,
    fontSize: '25px',
    fontWeight: '400',
    lineHeight: '30px',
    letterSpacing: '0em',
    textAlign: 'left',
    paddingLeft: '330px',
  },
  menu: {
    paddingLeft: '30px',
    paddingTop: '49px',
  },
  cardSet: {
    display: 'flex',
    marginTop: '70px',
    flexWrap: 'wrap',
    marginLeft: '350px',
    marginRight: '-30px',
    marginBottom: '-30px',
  },
  cardSet__item: {
    marginRight: '30px',
    marginBottom: '30px',
  },
  cardSet__link: {},
  card: {},
};

function Home() {
  return (
    <main style={styles.body} className="main-page">
      <section className="section hero">
        <div className="container hero__container">
          <h1 style={styles.hero__title} className="hero__title">
            Ласкаво просимо до нашого онлайн-магазину тютюну для кальяну!<br />
            У нас є широкий вибір смаків та марок, які задовольнять навіть найвибагливішого любителя кальяну.<br />
            Замовляйте прямо зараз і насолоджуйтесь вишуканим димом, створеним з найякіснішого тютюну.<br />
            Наші продукти гарантовано піднімуть настрій і додадуть неповторних відчуттів під час кальянної сесії.<br />
            Замовляйте вже сьогодні та насолоджуйтесь вишуканим досвідом кальянування разом з нами!
          </h1>
        </div>
      </section>
      <div style={styles.cardSet} className="card-set list">
        <div style={styles.cardSet__item} className="card-set__item">
          <a href="" style={styles.cardSet__link} className="card-set__link link">
            <div style={styles.card}>
              <img src="./img/goods.jpg" alt="" />
            </div>
          </a>
        </div>
      </div>
    </main>
  );
}

export default Home;
